import { useEffect, useState } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';
import ManageCourses from './ManageCourses';
import CreateCourseForm from './CreateCourseForm';
import EditCourseForm from './EditCourseForm';
import './style.css';

export default function App() {
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const [mainView, setMainView] = useState('main'); // 'main', 'manageUsers', 'manageCourses'
  const [userView, setUserView] = useState('list'); // 'list', 'create', 'edit'
  const [courseView, setCourseView] = useState('list'); // 'list', 'create', 'edit'

  const [editingUser, setEditingUser] = useState(null);
  const [editingCourse, setEditingCourse] = useState(null);

  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/users');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setUsers(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setUsers([]);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchCourses = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/courses');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setCourses(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setCourses([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (mainView === 'manageUsers') fetchUsers();
    if (mainView === 'manageCourses') fetchCourses();
  }, [mainView]);

  const handleEditUser = (user) => {
    setEditingUser(user);
    setUserView('edit');
  };

  const handleDeleteUser = async (email) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      const res = await fetch(`http://localhost:3001/users/${email}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete user');
      await fetchUsers();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setCourseView('edit');
  };

  const handleDeleteCourse = async (courseID) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    try {
      const res = await fetch(`http://localhost:3001/courses/${courseID}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete course');
      await fetchCourses();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleCancel = () => {
    setEditingUser(null);
    setEditingCourse(null);
    setUserView('list');
    setCourseView('list');
  };

  return (
    <div className="admin-dashboard" style={{ textAlign: 'center', padding: '20px' }}>
      {/* Main Dashboard */}
      {mainView === 'main' && (
        <>
          <header className="admin-header">
            <h2>Admin Dashboard</h2>
          </header>
          <button onClick={() => { setMainView('manageUsers'); setUserView('list'); }} style={{ margin: '10px' }}>
            Manage Users
          </button>
          <button onClick={() => { setMainView('manageCourses'); setCourseView('list'); }} style={{ margin: '10px' }}>
            Manage Courses
          </button>
        </>
      )}

      {/* Manage Users Section */}
      {mainView === 'manageUsers' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button onClick={() => { setUserView('create'); setEditingUser(null); }} style={{ marginRight: '10px' }}>
              Create User
            </button>
            <button onClick={() => setUserView('list')}>User List</button>
            <button onClick={() => { setMainView('main'); setUserView('list'); }} style={{ marginLeft: 10 }}>
              Back to Dashboard
            </button>
          </div>

          {isLoading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : userView === 'list' ? (
            <ManageUsers
              users={users}
              onDelete={handleDeleteUser}
              onEdit={handleEditUser}
              fetchUsers={fetchUsers}
            />
          ) : userView === 'create' ? (
            <CreateUserForm
              onUserCreated={() => { fetchUsers(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : userView === 'edit' ? (
            <EditUserForm
              user={editingUser}
              onUserUpdated={() => { fetchUsers(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}

      {/* Manage Courses Section */}
      {mainView === 'manageCourses' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button onClick={() => { setCourseView('create'); setEditingCourse(null); }} style={{ marginRight: '10px' }}>
              Create Course
            </button>
            <button onClick={() => setCourseView('list')}>Course List</button>
            <button onClick={() => { setMainView('main'); setCourseView('list'); }} style={{ marginLeft: 10 }}>
              Back to Dashboard
            </button>
          </div>

          {isLoading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : courseView === 'list' ? (
            <ManageCourses
              courses={courses}
              onEdit={handleEditCourse}
              onDelete={handleDeleteCourse}
              fetchCourses={fetchCourses}
            />
          ) : courseView === 'create' ? (
            <CreateCourseForm
              onCourseCreated={() => { fetchCourses(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : courseView === 'edit' ? (
            <EditCourseForm
              course={editingCourse}
              onCourseUpdated={() => { fetchCourses(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}
    </div>
  );
}
