import { useState, useEffect } from 'react';
import './style.css';

export default function EditCourseForm({ course, onCourseUpdated, onCancel }) {
  const [formData, setFormData] = useState({
    courseID: '',
    userID: '',
    Category: '',
    Completed: false,       // boolean
    Contents: '',
    Cost: 0,                // number
    CourseReview: '',
    Description: '',
    Enrolled: 0,            // number
    EstimatedDuration: '',
    Image: '',
    LastUpdatedOn: '',
    PublishedOn: '',
    Rating: 0,              // number
    Requirements: '',
    Title: '',
    WhatWeCoverInCourse: '',
    WhatYouLearn: '',
  });

  const [error, setError] = useState(null);

  useEffect(() => {
    if (course) {
      setFormData({
        courseID: course.courseID || '',
        userID: course.userID || '',
        Category: course.Category || '',
        Completed: course.Completed || false,
        Contents: course.Contents || '',
        Cost: course.Cost || 0,
        CourseReview: course.CourseReview || '',
        Description: course.Description || '',
        Enrolled: course.Enrolled || 0,
        EstimatedDuration: course.EstimatedDuration || '',
        Image: course.Image || '',
        LastUpdatedOn: course.LastUpdatedOn || '',
        PublishedOn: course.PublishedOn || '',
        Rating: course.Rating || 0,
        Requirements: course.Requirements || '',
        Title: course.Title || '',
        WhatWeCoverInCourse: course.WhatWeCoverInCourse || '',
        WhatYouLearn: course.WhatYouLearn || '',
      });
      setError(null);
    }
  }, [course]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : 
               type === 'number' ? Number(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch(`http://localhost:3001/courses/${encodeURIComponent(formData.courseID)}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const updatedCourse = await response.json();
      console.log('Course updated on server:', updatedCourse);

      onCourseUpdated();  // tell parent to refresh & close form
    } catch (err) {
      console.error(err);
      setError(err.message || 'Error updating course');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      {error && <div style={{ color: 'red', marginBottom: '1em' }}>{error}</div>}

      <input
        name="courseID"
        placeholder="Course ID"
        value={formData.courseID}
        onChange={handleChange}
        required
      />
      <input
        name="userID"
        placeholder="User ID"
        value={formData.userID}
        onChange={handleChange}
      />
      <input
        name="Category"
        placeholder="Category"
        value={formData.Category}
        onChange={handleChange}
      />

      <label>
        Completed
        <input
          type="checkbox"
          name="Completed"
          checked={formData.Completed}
          onChange={handleChange}
        />
      </label>

      <textarea
        name="Contents"
        placeholder="Contents"
        value={formData.Contents}
        onChange={handleChange}
      />

      <input
        type="number"
        name="Cost"
        placeholder="Cost"
        value={formData.Cost}
        onChange={handleChange}
        min="0"
      />

      <textarea
        name="CourseReview"
        placeholder="Course Review"
        value={formData.CourseReview}
        onChange={handleChange}
      />

      <textarea
        name="Description"
        placeholder="Description"
        value={formData.Description}
        onChange={handleChange}
      />

      <input
        type="number"
        name="Enrolled"
        placeholder="Enrolled"
        value={formData.Enrolled}
        onChange={handleChange}
        min="0"
      />

      <input
        name="EstimatedDuration"
        placeholder="Estimated Duration"
        value={formData.EstimatedDuration}
        onChange={handleChange}
      />

      <input
        name="Image"
        placeholder="Image URL"
        value={formData.Image}
        onChange={handleChange}
      />

      <input
        name="LastUpdatedOn"
        placeholder="Last Updated On"
        type="date"
        value={formData.LastUpdatedOn}
        onChange={handleChange}
      />

      <input
        name="PublishedOn"
        placeholder="Published On"
        type="date"
        value={formData.PublishedOn}
        onChange={handleChange}
      />

      <input
        type="number"
        name="Rating"
        placeholder="Rating"
        value={formData.Rating}
        onChange={handleChange}
        min="0"
        max="5"
        step="0.1"
      />

      <textarea
        name="Requirements"
        placeholder="Requirements"
        value={formData.Requirements}
        onChange={handleChange}
      />

      <input
        name="Title"
        placeholder="Title"
        value={formData.Title}
        onChange={handleChange}
        required
      />

      <textarea
        name="WhatWeCoverInCourse"
        placeholder="What We Cover In Course"
        value={formData.WhatWeCoverInCourse}
        onChange={handleChange}
      />

      <textarea
        name="WhatYouLearn"
        placeholder="What You Learn"
        value={formData.WhatYouLearn}
        onChange={handleChange}
      />

      <div className="button-group" style={{ marginTop: '1em' }}>
        <button type="submit">Save Changes</button>
        <button type="button" onClick={onCancel} style={{ marginLeft: '1em' }}>
          Cancel
        </button>
      </div>
    </form>
  );
}
