import { useEffect, useState } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';
import './style.css';

export default function App() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Controls which big view is shown: 'main' dashboard or 'manageUsers' area
  const [mainView, setMainView] = useState('main');

  // Inside ManageUsers area: 'list', 'create', or 'edit'

  // User currently being edited
  const [editingUser, setEditingUser] = useState(null);

  // Fetch user list from backend API
  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/users');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setUsers(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setUsers([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Fetch users on component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  // When Edit button clicked on a user
  const handleEditUser = (user) => {
    setEditingUser(user);
    setManageView('edit');
  };

  // When Delete button clicked on a user
  const handleDeleteUser = async (email) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this user?');
    if (!confirmDelete) return;

    try {
      const res = await fetch(`http://localhost:3001/users/${email}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete user');
      await fetchUsers();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  // Click handler to switch to Create User form
  const handleCreateClick = () => {
    setEditingUser(null);
    setManageView('create');
  };

  // Cancel and go back to user list buttons inside ManageUsers
  const handleCancel = () => {
    setEditingUser(null);
    setManageView('list');
  };

  return (
    <div className="admin-dashboard" style={{ textAlign: 'center', padding: '20px' }}>
      {/* Main Dashboard View */}
      {mainView === 'main' && (
        <>
          <header className="admin-header">
            <h2>Admin Dashboard</h2>
          </header>
          <button
            onClick={() => {
              setMainView('manageUsers');
              setManageView('list'); // default to list view
            }}
            style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
          >
            Manage Users
          </button>
        </>
      )}

      {/* Manage Users Area */}
      {mainView === 'manageUsers' && (
        <div>
          {/* Buttons to switch between create, list, or back to dashboard */}
          <div style={{ margin: '20px 0' }}>
            <button onClick={handleCreateClick} style={{ marginRight: 10 }}>
              Create User
            </button>
            <button onClick={() => setManageView('list')}>User List</button>
            <button
              onClick={() => {
                setMainView('main');
                setManageView('list');
              }}
              style={{ marginLeft: 20, color: 'red' }}
            >
              Back to Dashboard
            </button>
          </div>

          {/* Loading and Error states */}
          {isLoading ? (
            <p>Loading users...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : manageView === 'list' ? (
            // Show user list with edit/delete buttons
            <ManageUsers
              users={users}
              onDelete={handleDeleteUser}
              onEdit={handleEditUser}
              fetchUsers={fetchUsers}
            />
          ) : manageView === 'create' ? (
            // Show create user form
            <CreateUserForm
              onUserCreated={() => {
                fetchUsers();
                handleCancel();
              }}
              onCancel={handleCancel}
            />
          ) : manageView === 'edit' ? (
            // Show edit user form
            <EditUserForm
              user={editingUser}
              onUserUpdated={() => {
                fetchUsers();
                handleCancel();
              }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}
    </div>
  );
}
