import { useState, useEffect } from 'react';
import './style.css';

export default function CreateCourseForm({ course, onCourseCreated, onCourseUpdated, onCancel }) {
  const [formData, setFormData] = useState({
    courseID: '',
    userID: '',
    Category: '',
    Completed: false,
    Contents: '',
    Cost: '',
    CourseReview: '',
    Description: '',
    Enrolled: 0,
    EstimatedDuration: '',
    Image: '',
    LastUpdatedOn: '',
    PublishedOn: '',
    Rating: 0,
    Requirements: '',
    Title: '',
    WhatWeCoverInCourse: '',
    WhatYouLearn: '',
  });

  const [error, setError] = useState(null);

  useEffect(() => {
    if (course) {
      setFormData({
        ...course,
        Completed: course.Completed || false,
        Enrolled: course.Enrolled || 0,
        Rating: course.Rating || 0,
      });
    } else {
      // Reset form on create
      setFormData({
        courseID: '',
        userID: '',
        Category: '',
        Completed: false,
        Contents: '',
        Cost: '',
        CourseReview: '',
        Description: '',
        Enrolled: 0,
        EstimatedDuration: '',
        Image: '',
        LastUpdatedOn: '',
        PublishedOn: '',
        Rating: 0,
        Requirements: '',
        Title: '',
        WhatWeCoverInCourse: '',
        WhatYouLearn: '',
      });
      setError(null);
    }
  }, [course]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Convert Enrolled and Rating to numbers
    const dataToSend = {
      ...formData,
      Enrolled: Number(formData.Enrolled) || 0,
      Rating: Number(formData.Rating) || 0,
    };

    try {
      const url = course
        ? `http://localhost:3001/courses/${course.courseID || course.userID}`
        : 'http://localhost:3001/courses';

      const method = course ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSend),
      });

      if (!response.ok) {
        throw new Error('Failed to save course: ' + response.statusText);
      }

      if (course) {
        onCourseUpdated();
      } else {
        onCourseCreated();
      }
    } catch (err) {
      console.error('Error saving course:', err);
      setError(err.message || 'Failed to save course');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      <div className="form-group">
        <label htmlFor="courseID">Course ID:</label>
        <input
          name="courseID"
          placeholder="Course ID"
          value={formData.courseID}
          onChange={handleChange}
          required={!course}
          disabled={!!course} // disable editing courseID if editing existing course
        />
      </div>

      <div className="form-group">
        <label htmlFor="userID">User ID (Creator):</label>
        <input
          name="userID"
          placeholder="User ID"
          value={formData.userID}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Category">Category:</label>
        <input
          name="Category"
          placeholder="Category"
          value={formData.Category}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Completed">Completed:</label>
        <input
          type="checkbox"
          name="Completed"
          checked={formData.Completed}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Contents">Contents:</label>
        <textarea
          name="Contents"
          placeholder="Contents"
          value={formData.Contents}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Cost">Cost:</label>
        <input
          name="Cost"
          placeholder="Cost"
          value={formData.Cost}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="CourseReview">Course Review:</label>
        <textarea
          name="CourseReview"
          placeholder="Course Review"
          value={formData.CourseReview}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Description">Description:</label>
        <textarea
          name="Description"
          placeholder="Description"
          value={formData.Description}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Enrolled">Enrolled:</label>
        <input
          name="Enrolled"
          type="number"
          min="0"
          placeholder="Enrolled"
          value={formData.Enrolled}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="EstimatedDuration">Estimated Duration:</label>
        <input
          name="EstimatedDuration"
          placeholder="Estimated Duration"
          value={formData.EstimatedDuration}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Image">Image URL:</label>
        <input
          name="Image"
          placeholder="Image URL"
          value={formData.Image}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="LastUpdatedOn">Last Updated On:</label>
        <input
          name="LastUpdatedOn"
          placeholder="Last Updated On"
          value={formData.LastUpdatedOn}
          onChange={handleChange}
          type="date"
        />
      </div>

      <div className="form-group">
        <label htmlFor="PublishedOn">Published On:</label>
        <input
          name="PublishedOn"
          placeholder="Published On"
          value={formData.PublishedOn}
          onChange={handleChange}
          type="date"
        />
      </div>

      <div className="form-group">
        <label htmlFor="Rating">Rating:</label>
        <input
          name="Rating"
          type="number"
          min="0"
          max="5"
          step="0.1"
          placeholder="Rating"
          value={formData.Rating}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Requirements">Requirements:</label>
        <textarea
          name="Requirements"
          placeholder="Requirements"
          value={formData.Requirements}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="Title">Title:</label>
        <input
          name="Title"
          placeholder="Title"
          value={formData.Title}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="WhatWeCoverInCourse">What We Cover In Course:</label>
        <textarea
          name="WhatWeCoverInCourse"
          placeholder="What We Cover In Course"
          value={formData.WhatWeCoverInCourse}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="WhatYouLearn">What You Learn:</label>
        <textarea
          name="WhatYouLearn"
          placeholder="What You Learn"
          value={formData.WhatYouLearn}
          onChange={handleChange}
        />
      </div>

      <div className="button-group">
        <button type="submit">{course ? 'Save Changes' : 'Create Course'}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
    </form>
  );
}
