import { useState, useEffect } from 'react';
import './style.css';

export default function CreateCourseForm({ course, onCourseCreated, onCourseUpdated, onCancel }) {
 

  const [error, setError] = useState(null);

  useEffect(() => {
    if (course) {
      setFormData({ ...course });
    } else {
      setFormData({
        courseID: '',
        userID: '',
        Category: '',
        Completed: false,
        Contents: '',
        Cost: '',
        CourseReview: '',
        Description: '',
        Enrolled: '',
        EstimatedDuration: '',
        Image: '',
        LastUpdatedOn: '',
        PublishedOn: '',
        Rating: '',
        Requirements: '',
        Title: '',
        WhatWeCoverInCourse: '',
        WhatYouLearn: '',
      });
      setError(null);
    }
  }, [course]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    const url = course
  ? `http://localhost:5000/courses/${course.courseID}`
  : 'http://localhost:5000/courses';

    const method = course ? 'PUT' : 'POST';

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to save course: ' + response.statusText);
      }

      course ? onCourseUpdated() : onCourseCreated();
    } catch (err) {
      console.error('Error saving course:', err);
      setError(err.message || 'Failed to save course');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      {error && <div className="error-message" style={{ color: 'red' }}>{error}</div>}

      {Object.entries(formData).map(([key, value]) => (
        <div className="form-group" key={key}>
          <label htmlFor={key}>{key}:</label>
          {typeof value === 'boolean' ? (
            <input
              type="checkbox"
              name={key}
              checked={value}
              onChange={handleChange}
            />
          ) : (
            <input
              name={key}
              placeholder={key}
              value={value}
              onChange={handleChange}
            />
          )}
        </div>
      ))}

      <div className="button-group">
        <button type="submit">{course ? 'Save Changes' : 'Create Course'}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
    </form>
  );
}
