import { useEffect, useState } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';

import './style.css';

export default function App() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Controls the big view state: 'main' or 'manageUsers'
  const [mainView, setMainView] = useState('main');

  // Inside manageUsers, control these views: 'list', 'create', 'edit'
  const [manageView, setManageView] = useState('list');
  const [editingUser, setEditingUser] = useState(null);

  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/users');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setUsers(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setUsers([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleEditUser = (user) => {
    setEditingUser(user);
    setManageView('edit');
  };

  const handleDeleteUser = async (email) => {
    const confirm = window.confirm('Are you sure you want to delete this user?');
    if (!confirm) return;

    try {
      const res = await fetch(`http://localhost:3001/users/${email}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete user');
      await fetchUsers();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleCreateClick = () => {
    setEditingUser(null);
    setManageView('create');
  };

  const handleCancel = () => {
    setEditingUser(null);
    setManageView('list');
  };

  return (
    <div className="admin-dashboard" style={{ textAlign: 'center', padding: '20px' }}>
      <header className="admin-header">
        <h2>Admin Dashboard</h2>
      </header>

      {mainView === 'main' && (
        <button
          onClick={() => {
            setMainView('manageUsers');
            setManageView('list'); // default view inside ManageUsers
          }}
          style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
        >
          Manage Users
        </button>
      )}

      {mainView === 'manageUsers' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button onClick={handleCreateClick} style={{ marginRight: 10 }}>
              Create User
            </button>
            <button onClick={() => setManageView('list')}>User List</button>
            <button
              onClick={() => {
                setMainView('main');
                setManageView('list');
              }}
              style={{ marginLeft: 20, color: 'red' }}
            >
              Back to Dashboard
            </button>
          </div>

          {isLoading ? (
            <p>Loading users...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : manageView === 'list' ? (
            <ManageUsers
              users={users}
              onDelete={handleDeleteUser}
              onEdit={handleEditUser}
              fetchUsers={fetchUsers}
            />
          ) : manageView === 'create' ? (
            <CreateUserForm
              onUserCreated={() => {
                fetchUsers();
                handleCancel();
              }}
              onCancel={handleCancel}
            />
          ) : manageView === 'edit' ? (
            <EditUserForm
              user={editingUser}
              onUserUpdated={() => {
                fetchUsers();
                handleCancel();
              }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}
    </div>
  );
}
