import { useEffect, useState } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';
import ManageCourses from './ManageCourses';
import CreateCourseForm from './CreateCourseForm';
import EditCourseForm from './EditCourseForm'; // imported EditCourseForm
import './style.css';

export default function App() {
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const [mainView, setMainView] = useState('main'); // 'main', 'manageUsers', 'manageCourses'
  const [manageView, setManageView] = useState();   // 'list', 'create', 'edit'

  const [editingUser, setEditingUser] = useState(null);
  const [editingCourse, setEditingCourse] = useState(null); // add state for editing course

  // USERS: fetch from backend
  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/users');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setUsers(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setUsers([]);
    } finally {
      setIsLoading(false);
    }
  };

  // COURSES: fetch from backend
  const fetchCourses = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:3001/courses');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setCourses(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      setCourses([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (mainView === 'manageUsers') fetchUsers();
    if (mainView === 'manageCourses') fetchCourses();
  }, [mainView]);

  // Edit user handlers
  const handleEditUser = (user) => {
    setEditingUser(user);
    setManageView('edit');
  };

  const handleDeleteUser = async (email) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      const res = await fetch(`http://localhost:3001/users/${email}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete user');
      await fetchUsers();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  // Edit course handlers
  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setManageView('edit');
  };

  const handleDeleteCourse = async (courseID) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    try {
      const res = await fetch(`http://localhost:3001/courses/${courseID}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete course');
      await fetchCourses();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleCancel = () => {
    setEditingUser(null);
    setEditingCourse(null);
    setManageView('list');
  };

  return (
    <div className="admin-dashboard" style={{ textAlign: 'center', padding: '20px' }}>
      {/* Dashboard View */}
      {mainView === 'main' && (
        <>
          <header className="admin-header">
            <h2>Admin Dashboard</h2>
          </header>
          <button onClick={() => { setMainView('manageUsers'); setManageView('list'); }} style={{ margin: '10px' }}>
            Manage Users
          </button>
          <button onClick={() => { setMainView('manageCourses'); setManageView('list'); }} style={{ margin: '10px' }}>
            Manage Courses
          </button>
        </>
      )}

      {/* Manage Users */}
      {mainView === 'manageUsers' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button
              onClick={() => { setManageView('create'); setEditingUser(null); }}
              style={{ marginRight: '10px' }}
            >
              Create User
            </button>
            <button onClick={() => setManageView('list')}>User List</button>
            <button
              onClick={() => { setMainView('main'); setManageView('list'); }}
              style={{ marginLeft: 10 }}
            >
              Back to Dashboard
            </button>
          </div>

          {isLoading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : manageView === 'list' ? (
            <ManageUsers
              users={users}
              onDelete={handleDeleteUser}
              onEdit={handleEditUser}
              fetchUsers={fetchUsers}
            />
          ) : manageView === 'create' ? (
            <CreateUserForm
              onUserCreated={() => { fetchUsers(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : manageView === 'edit' ? (
            <EditUserForm
              user={editingUser}
              onUserUpdated={() => { fetchUsers(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}

      {/* Manage Courses */}
      {mainView === 'manageCourses' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button onClick={() => { setManageView('create'); setEditingCourse(null); }} style={{ marginRight: '10px' }}>
              Create Course
            </button>
            <button onClick={() => setManageView('list')}>Course List</button>
            <button
              onClick={() => { setMainView('main'); setManageView('list'); }}
              style={{ marginLeft: 10 }}
            >
              Back to Dashboard
            </button>
          </div>

          {isLoading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : manageView === 'list' ? (
            <ManageCourses
              courses={courses}
              onEdit={handleEditCourse}         // Pass edit handler to ManageCourses
              onDelete={handleDeleteCourse}     // Pass delete handler to ManageCourses
              fetchCourses={fetchCourses}
            />
          ) : manageView === 'create' ? (
            <CreateCourseForm
              onCourseCreated={() => { fetchCourses(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : manageView === 'edit' ? (
            <EditCourseForm
              course={editingCourse}              // Pass course to edit
              onCourseUpdated={() => { fetchCourses(); handleCancel(); }}
              onCancel={handleCancel}
            />
          ) : null}
        </div>
      )}
    </div>
  );
}
