import { useState, useEffect } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';
// no imports for courses yet

export default function App() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const [mainView, setMainView] = useState('main');
  const [manageView, setManageView] = useState('list');
  const [editingUser, setEditingUser] = useState(null);

  // Fetch users, handlers etc. here...

  // New state to track manageCourses view buttons (list or create)
  const [manageCoursesView, setManageCoursesView] = useState(null);

  // Handlers for manage users...
  // Your existing code here...

  return (
    <div className="admin-dashboard" style={{ textAlign: 'center', padding: '20px' }}>
      {/* Main Dashboard */}
      {mainView === 'main' && (
        <>
          <header className="admin-header">
            <h2>Admin Dashboard</h2>
          </header>
          <button
            onClick={() => {
              setMainView('manageUsers');
              setManageView('list');
            }}
            style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
          >
            Manage Users
          </button>
          <button
            onClick={() => {
              setMainView('manageCourses');
              setManageCoursesView(null);
            }}
            style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px', marginLeft: 10 }}
          >
            Manage Courses
          </button>
        </>
      )}

      {/* Manage Users Area */}
      {mainView === 'manageUsers' && (
        <div>
          <div style={{ margin: '20px 0' }}>
            <button onClick={() => { setEditingUser(null); setManageView('create'); }} style={{ marginRight: 10 }}>
              Create User
            </button>
            <button onClick={() => setManageView('list')}>User List</button>
            <button
              onClick={() => {
                setMainView('main');
                setManageView('list');
              }}
              style={{ marginLeft: 20, color: 'red' }}
            >
              Back to Dashboard
            </button>
          </div>

          {/* Loading and Error states */}
          {isLoading ? (
            <p>Loading users...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : manageView === 'list' ? (
            <ManageUsers users={users} onDelete={() => {}} onEdit={() => {}} fetchUsers={() => {}} />
          ) : manageView === 'create' ? (
            <CreateUserForm onUserCreated={() => {}} onCancel={() => setManageView('list')} />
          ) : manageView === 'edit' ? (
            <EditUserForm user={editingUser} onUserUpdated={() => {}} onCancel={() => setManageView('list')} />
          ) : null}
        </div>
      )}

      {/* Manage Courses Area - just buttons, no components yet */}
      {mainView === 'manageCourses' && (
        <div style={{ marginTop: '20px' }}>
          <button
            onClick={() => setManageCoursesView('create')}
            style={{ marginRight: 10 }}
          >
            Create Course
          </button>
          <button onClick={() => setManageCoursesView('list')}>
            Courses List
          </button>
          <button
            onClick={() => setMainView('main')}
            style={{ marginLeft: 20, color: 'red' }}
          >
            Back to Dashboard
          </button>

          {/* Show which course button is selected */}
          {manageCoursesView === 'create' && <p>You clicked Create Course (UI not ready)</p>}
          {manageCoursesView === 'list' && <p>You clicked Courses List (UI not ready)</p>}
        </div>
      )}
    </div>
  );
}
