import { useEffect, useState } from 'react';
import ManageUsers from './ManageUsers';
import CreateUserForm from './CreateUserForm';
import EditUserForm from './EditUserForm';
import ManageCourses from './ManageCourses';
import CreateCourseForm from './CreateCourseForm';
import EditCourseForm from './EditCourseForm';

export default function App() {
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [userError, setUserError] = useState(null);
  const [courseError, setCourseError] = useState(null);
  const [isUsersLoading, setIsUsersLoading] = useState(false);
  const [isCoursesLoading, setIsCoursesLoading] = useState(false);
  const [mainView, setMainView] = useState('main');
  const [manageView, setManageView] = useState('');
  const [editingUser, setEditingUser] = useState(null);
  const [editingCourse, setEditingCourse] = useState(null);

  const fetchUsers = async () => {
    setIsUsersLoading(true);
    try {
      const response = await fetch('http://localhost:3001/users');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setUsers(data);
      setUserError(null);
    } catch (err) {
      setUserError(err.message);
      setUsers([]);
    } finally {
      setIsUsersLoading(false);
    }
  };

  const fetchCourses = async () => {
    setIsCoursesLoading(true);
    try {
      const response = await fetch('http://localhost:3001/courses');
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setCourses(data);
      setCourseError(null);
    } catch (err) {
      setCourseError(err.message);
      setCourses([]);
    } finally {
      setIsCoursesLoading(false);
    }
  };

  useEffect(() => {
    if (mainView === 'manageUsers' && manageView === 'list') {
      fetchUsers();
    }
    if (mainView === 'manageCourses' && manageView === 'list') {
      fetchCourses();
    }
  }, [mainView, manageView]);

  const handleEditUser = (user) => {
    setEditingUser(user);
    setManageView('edit');
  };

  const handleDeleteUser = async (email) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      const res = await fetch(`http://localhost:3001/users/${email}`, { method: 'DELETE' });
      if (!res.ok) throw new Error('Failed to delete user');
      await fetchUsers();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setManageView('edit');
  };

  const handleDeleteCourse = async (courseId, userId) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    try {
      const res = await fetch(`http://localhost:3001/courses/${courseId}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId }),
      });
      if (!res.ok) throw new Error('Failed to delete course');
      await fetchCourses();
    } catch (err) {
      alert('Delete failed: ' + err.message);
    }
  };

  const handleCancel = () => {
    setEditingUser(null);
    setEditingCourse(null);
    setManageView('list');
  };

  const styles = {
    page: {
      height: '100vh',
      backgroundColor: '#f0f2f5',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'flex-start',
      paddingTop: '30px',
    },
    container: {
      backgroundColor: '#ffffff',
      padding: '30px 30px',
      borderRadius: '12px',
      boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
      width: '90%',
      maxWidth: '900px',
    },
    title: {
      textAlign: 'center',
      marginBottom: '25px',
      color: '#1f2937',
      fontSize: '22px',
    },
    buttonRow: {
      display: 'flex',
      justifyContent: 'center',
      flexWrap: 'wrap',
      gap: '12px',
      marginBottom: '25px',
    },
    button: {
      padding: '10px 20px',
      fontSize: '15px',
      fontWeight: '600',
      backgroundColor: '#3b82f6',
      color: '#fff',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      transition: 'background 0.2s ease-in-out',
    },
    content: {
      marginTop: '10px',
    },
  };

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h2 style={styles.title}>Admin Dashboard</h2>

        {mainView === 'main' && (
          <div style={styles.buttonRow}>
            <button style={styles.button} onClick={() => { setMainView('manageUsers'); setManageView(''); }}>Manage Users</button>
            <button style={styles.button} onClick={() => { setMainView('manageCourses'); setManageView(''); }}>Manage Courses</button>
          </div>
        )}

        {mainView === 'manageUsers' && (
          <div>
            <div style={styles.buttonRow}>
              <button style={styles.button} onClick={() => { setManageView('create'); setEditingUser(null); }}>Create User</button>
              <button style={styles.button} onClick={() => { setManageView('list'); fetchUsers(); }}>User List</button>
              <button style={styles.button} onClick={() => setMainView('main')}>Back to Dashboard</button>
            </div>

            <div style={styles.content}>
              {isUsersLoading ? <p>Loading...</p> : userError ? <p>Error: {userError}</p> :
                manageView === 'list' ? (
                  <ManageUsers users={users} onDelete={handleDeleteUser} onEdit={handleEditUser} fetchUsers={fetchUsers} />
                ) : manageView === 'create' ? (
                  <CreateUserForm onUserCreated={() => { fetchUsers(); handleCancel(); }} onCancel={handleCancel} />
                ) : manageView === 'edit' && editingUser ? (
                  <EditUserForm user={editingUser} onUserUpdated={() => { fetchUsers(); handleCancel(); }} onCancel={handleCancel} />
                ) : null}
            </div>
          </div>
        )}

        {mainView === 'manageCourses' && (
          <div>
            <div style={styles.buttonRow}>
              <button style={styles.button} onClick={() => { setManageView('create'); setEditingCourse(null); }}>Create Course</button>
              <button style={styles.button} onClick={() => { setManageView('list'); fetchCourses(); }}>Course List</button>
              <button style={styles.button} onClick={() => setMainView('main')}>Back to Dashboard</button>
            </div>

            <div style={styles.content}>
              {isCoursesLoading ? <p>Loading...</p> : courseError ? <p>Error: {courseError}</p> :
                manageView === 'list' ? (
                  <ManageCourses courses={courses} onDelete={handleDeleteCourse} onEdit={handleEditCourse} fetchCourses={fetchCourses} />
                ) : manageView === 'create' ? (
                  <CreateCourseForm onCourseCreated={() => { fetchCourses(); handleCancel(); }} onCancel={handleCancel} />
                ) : manageView === 'edit' && editingCourse ? (
                  <EditCourseForm course={editingCourse} onCourseUpdated={() => { fetchCourses(); handleCancel(); }} onCancel={handleCancel} />
                ) : null}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
